import {Component, Input} from '@angular/core';
import {Car, CarState} from "../Domain/CarConstants";
import {FormBuilder, FormGroup} from "@angular/forms";
import {HttpClient} from "@angular/common/http";

@Component({
  selector: 'app-car-state-machine',
  templateUrl: './car-state-machine.component.html',
  styleUrls: ['./car-state-machine.component.css']
})
export class CarStateMachineComponent {
  @Input() public car!: Car;
  public states: (string | CarState)[] = Object.values(CarState).filter((x: string | CarState) => !isNaN(parseInt(x.toString())));

  form: FormGroup;

  constructor(private http: HttpClient, builder: FormBuilder) {
    this.form = builder.group({
      first: "",
    });
  }

  protected readonly CarState = CarState;
  protected readonly Object = Object;
  protected readonly parseInt = parseInt;
  protected readonly isNaN = isNaN;


  public GoToNextState(): void {
    let body = {id: this.car.id}

    this.http.post<Car>(`https://localhost:7276/api/VehicleStateMachine/vehicle`, body).subscribe(result => {
      console.log(result)
    }, error => console.error(error));
  }

}
<div class="car-info">
  <p class="car-info-item">Car Id : {{car.id}},</p>
  <p class="car-info-item">State : {{ CarState[car!.state] }},</p>
  <p class="car-info-item">Speed {{ car.speed }}</p>
</div>
<br>
<div class="state-machine-states">
  <ng-container *ngFor="let state of states">
    <div class="inner-state">
      <div *ngIf="car.state == state" class="rounded-green-circle"></div>
      <div *ngIf="car.state > state" class="rounded-circle"></div>
      {{CarState[+state]}}
    </div>
  </ng-container>
</div>

<br>
<button class="btn btn-primary" (click)="GoToNextState()">search</button>
